REPLACE INTO `items_localized` VALUES (43633,'deDE','Prinz Kael\'thas Sonnenwanderers Goldmünze','Dieser junge Lehrling namens Jaina ist ziemlich attraktiv für einen Menschen. Ich hoffe, dass sie erkennt, wie elegant und mächtig ich bin.');
