INSERT INTO quests_localized VALUES ("8697", "zhCN", "无尽智慧之戒", "据说其拉虫人的军官所佩戴的戒指可以给他们带来无穷的能量。那些戒指上镶嵌着腐化的宝石，也许我们可以用纯净的材料替换它们，以此来制作净化版本的戒指。$b$b那些装饰在其拉雕像上的宝石就非常合适，它们的质量非常好。$b$b把戒指和雕像交给我，我会为你制作一枚强大的戒指，$c。", "将1枚其拉典礼戒指、2个黑曜石雕像、5个银质甲虫和5个白骨甲虫交给唤风者耶萨德拉。你必须在塞纳里奥议会中达到尊敬声望才能完成这个任务。", "", "", "", "", "", "", "");
